package cn.wolfcode.crm.service.iml;import cn.wolfcode.crm.domain.Dirname;import cn.wolfcode.crm.mapper.DirnameMapper;import cn.wolfcode.crm.service.IDirnameService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;/** * Created by felix on 2017/12/23. */@Servicepublic class DirnameServiceImpl implements IDirnameService {	@Autowired	private DirnameMapper mapper;	@Override	public void deleteByPrimaryKey(Long id) {		mapper.deleteByPrimaryKey(id);	}	@Override	public void insert(Dirname record) {			mapper.insert(record);	}	@Override	public Dirname selectByPrimaryKey(Long id) {		return mapper.selectByPrimaryKey(id);	}	@Override	public List<Dirname> selectAll() {		return mapper.selectAll();	}	@Override	public void updateByPrimaryKey(Dirname record) {			mapper.updateByPrimaryKey(record);	}	@Override	public Dirname selectParentByName(String name) {		return mapper.selectParentByName(name);	}	@Override	public List<Dirname> selectDirnameBymenuId(Long id) {		return mapper.selectDirnameBymenuId(id);	}	@Override	public List<Dirname> selectDirnemByParentId(Long id) {		return mapper.selectDirnemByParentId(id);	}}