package cn.wolfcode.crm.service.iml;import cn.wolfcode.crm.domain.Unit;import cn.wolfcode.crm.mapper.UnitMapper;import cn.wolfcode.crm.service.IUnitService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic class UnitServiceImpl implements IUnitService {	@Autowired	private UnitMapper mapper;	@Override	public void deleteByPrimaryKey(Long id) {		mapper.deleteByPrimaryKey(id);	}	@Override	public void insert(Unit record) {		mapper.insert(record);	}	@Override	public Unit selectByPrimaryKey(Long id) {		return mapper.selectByPrimaryKey(id);	}	@Override	public List<Unit> selectAll() {		return mapper.selectAll();	}	@Override	public void updateByPrimaryKey(Unit record) {	}	@Override	public Unit selectUnitByName(String name) {		return mapper.selectUnitByName(name);	}}