package cn.wolfcode.crm.web.controller;import cn.wolfcode.crm.annotion.PermissionName;import cn.wolfcode.crm.domain.Unit;import cn.wolfcode.crm.service.IUnitService;import org.apache.shiro.authz.annotation.RequiresPermissions;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.ResponseBody;import java.util.List;@Controller@RequestMapping("unit")public class UnitController {	@Autowired	private IUnitService unitService;	@RequestMapping("selectAll")	@PermissionName("单位列表")	@RequiresPermissions("menu:selectAll")	@ResponseBody	public List<Unit> selectAll() {		List<Unit> units = unitService.selectAll();		return units;	}}